class TangentNormalExample(Scene):
    def construct(self):
        ax = Axes(
            x_range=[-2, 8],
            y_range=[-1, 9],
            tips=True,
            #axis_config={
            #    "include_ticks": False,
            #    "include_numbers": True
            #}
        ).add_coordinates()

        # f(x) = x^2
        graph = ax.plot(lambda x: x**2, color=BLUE)
        graph_label = ax.get_graph_label(graph, label=MathTex("f(x)=x^2"), x_val=2.2, direction=UP)

        # åˆ‡ç·šèˆ‡æ³•ç·š
        x0 = 1
        y0 = x0**2
        derivative = 2 * x0

    
        normal_slope = -1 / derivative
        
        dot = Dot(ax.c2p(x0, y0), color=WHITE)

        # æ–¹ç¨‹å¼æ¨™ç±¤
        tangent_eq = MathTex(r"y - y_0 = f'(x_0)(x - x_0)", color=YELLOW).scale(0.7)
        normal_eq = MathTex(r"y - y_0 = -\frac{1}{f'(x_0)}(x - x_0)", color=RED).scale(0.7)
        # x_range æ‹‰é•·ï¼š
        tangent = ax.plot(lambda x: derivative * (x - x0) + y0, color=YELLOW, x_range=[-2, 5])
        normal = ax.plot(lambda x: normal_slope * (x - x0) + y0, color=RED, x_range=[-2, 5])
        # âœ… ç§»åˆ°ç¬¬äºŒè±¡é™
        tangent_eq.move_to(ax.c2p(-1, 8))
        normal_eq.move_to(ax.c2p(-1, 6))

        self.play(Create(ax))
        self.play(Create(graph), Write(graph_label))
        self.play(FadeIn(dot))
        self.play(Create(tangent), FadeIn(tangent_eq))
