class InflectionPointExample(Scene):
    def construct(self):
        # 坐標軸
        ax = Axes(
            x_range=[-3, 3, 1],
            y_range=[-10, 10, 5],
            tips=False,
        ).add_coordinates()

        # 函數曲線
        cubic_graph = ax.plot(lambda x: x**3, color=BLUE, stroke_width=4)
        cubic_label = ax.get_graph_label(cubic_graph, label=MathTex("f(x)=x^3"), x_val=2.2, direction=UP)

        # 拐點標記
        dot = Dot(ax.c2p(0, 0), color=YELLOW)
        dot_label = MathTex("x=0\\text{ 拐點}").next_to(dot, UP)

        # 二階導數文字說明
        text = MathTex(r"f''(x) = 6x").to_edge(UP)
        sign_left = MathTex(r"f''(x) &lt; 0").next_to(ax.c2p(-2, -8), LEFT)
        sign_right = MathTex(r"f''(x) &gt; 0").next_to(ax.c2p(2, 8), RIGHT)

        # 動畫
        self.play(Create(ax))
        self.play(Create(cubic_graph), Write(cubic_label))
        self.wait(1)
        self.play(FadeIn(dot), Write(dot_label))
        self.wait(1)
        self.play(Write(text))
        self.wait(0.5)
        self.play(Write(sign_left), Write(sign_right))
